import React from 'react';
import './App.css';
import classNames from "classnames";
//import DataTable from 'react-data-table-component';
import { Tab } from 'carbon-components';
import { DataTable } from 'carbon-components-react';
const {TableContainer, Table, TableHead, TableRow, TableBody, TableCell, TableHeader} = DataTable;

const headerData = [
  {
    key: 'name',
    header: 'Name',
  },
  {
    key: 'role',
    header: 'Role',
  },
  {
    key: 'status',
    header: 'Status',
  },
];

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {date: new Date(),
                  showLicencedUser: true,
                  tableData:[] ,
                  tableDataAll:[],
                  showSubscriptionUser:false,
                  showPendingUser:false,
                  loadlicbuttonFlag:false,
                  loadsubsbuttonFlag:false,
                  loadPendButtonFlag:false,
                  apiResponse:'',
                  inputSearchValue:''};
    this.loadLicencedUser = this.loadLicencedUser.bind(this);
    this.loadSubscriptionUser = this.loadSubscriptionUser.bind(this);
    this.loadPendingUser = this.loadPendingUser.bind(this);
    this.handleFilter = this.handleFilter.bind(this);
    this.searchString = this.searchString.bind(this);
    this.putValue =this.putValue.bind(this);
  }
  callAPI() {
    fetch("http://localhost:9000/tableDataAPI")
        .then(res => res.text())
      //  .then(res =>  abc= res);
        .then(res => this.setState({ apiResponse: res}));

//console.log("-----"+abc);
//    }, 5000);

        //this.setState({tableData: this.state.apiResponse});
   }
   putValue(){
     var val =this.state.apiResponse;
     console.log("+++++++++val"+JSON.parse(val).data);
     this.setState({tableData:JSON.parse(val).data});

     this.setState({tableDataAll:JSON.parse(val).data});
   }
  componentDidMount() {
   this.callAPI();
   let that =this;
   setTimeout(function() {
     that.putValue();
        }, 750);
  }

  componentWillUnmount() {

  }
  addSingleUser(e) {
    e.preventDefault();
    console.log("abc");
  }
  addMultipleUser(e) {
    e.preventDefault();
    console.log('Add multiple User Clicked.');
  }
  loadLicencedUser(e) {
    e.preventDefault();
    var data=this.state.tableDataAll;
    var dataLic = [] ;
    var j=0;
    for(var i=0;i<data.length;i++){
    var datarole =(data[i]).role;
      if(datarole.includes('Liscenced')){
        dataLic[j]=data[i];
        j++;
      }
    }
    this.setState({tableData:dataLic});
  }
  loadSubscriptionUser(e){
      e.preventDefault();
      var data=this.state.tableDataAll;
      var dataSubs = [] ;
      var j=0;
      for(var i=0;i<data.length;i++){
      var datarole =(data[i]).role;
        if(datarole.includes('Subscription')){
          dataSubs[j]=data[i];
          j++;
        }
      }
      this.setState({tableData:dataSubs});
  }
  loadPendingUser(e){
    console.log('load Pending User.');
      e.preventDefault();
      var data=this.state.tableDataAll;
      var dataPend = [] ;
      var j=0;
      for(var i=0;i<data.length;i++){
      var datarole =(data[i]).role;
        if(datarole.includes('Pending')){
          dataPend[j]=data[i];
          j++;
        }
      }
      this.setState({tableData:dataPend});
  }

  handleFilter(e){
    e.preventDefault();
    this.setState({inputSearchValue: e.target.value});
    var data=this.state.tableDataAll;
    if(e.target.value === ''){
      this.setState({tableData:data});
      console.log('Input is blanck')

    }
  }
  searchString(e){
    e.preventDefault();
    let input = this.state.inputSearchValue,
        data=this.state.tableDataAll,
        dataPend = [] ,
        j=0;
    console.log('input'+input);
    for(var i=0;i<data.length;i++){
    var datarole =(data[i]).name;
      if(datarole.includes(input)){
        dataPend[j]=data[i];
        j++;
      }
    }
    this.setState({tableData:dataPend});
  }

  render() {
    return (
      <div className="page-wrapper">
         <div className="row">
                        <div className="col-lg-12">
                            <div id="headerText" className="row">
                                <div className="col-lg-12">
                                    <h2>Manage Users</h2>
                                </div>
                            </div>
                        </div>
         </div>

    				<a id="addSingleUser" className="App-link" href="#" onClick={this.addSingleUser}>
            <i className="fa fa-plus"></i> Add New user </a>&nbsp;&nbsp;&nbsp;
    				<a id="addMultipleUser" className="App-link" href="#" onClick={this.addMultipleUser}>
            <i className="fa fa-plus"></i>Add Multiple user </a>
    				<hr/>
            <div data-tabs className="bx--tabs">
                  <div className="bx--tabs-trigger" tabIndex="0">
                    <a href="#" className="bx--tabs-trigger-text" tabIndex="-1"></a>
                    <svg focusable="false" preserveAspectRatio="xMidYMid meet" xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 16 16" aria-hidden="true"><path d="M8 11L3 6 3.7 5.3 8 9.6 12.3 5.3 13 6z"></path></svg>
                  </div>
                  <ul className="bx--tabs__nav bx--tabs__nav--hidden" role="tablist">
                    <li className="bx--tabs__nav-item bx--tabs__nav-item--selected " data-target=".tab-1-default" role="tab" aria-selected="true" >
                      <a tabIndex="0" id="tab-link-1-default" className="bx--tabs__nav-link" href="#" role="tab" aria-controls="tab-panel-1-default">Tab label 1</a>
                    </li>
                    <li className="bx--tabs__nav-item " data-target=".tab-2-default" role="tab" >
                      <a tabIndex="0" id="tab-link-2-default" className="bx--tabs__nav-link" href="#" role="tab"
                      aria-controls="tab-panel-2-default" onClick={this.loadSubscriptionUser}>Subscription Administrator</a>
